import './App.css'
import axios from 'axios'
import {useState, useEffect} from 'react'
//import {useParams} from 'react-router-dom'

function App() {
  //const params = useParams()

  const [loading, setLoading] = useState(true)
  const [article, setArticle] = useState('')
  const [idArticle, setIdArticle] = useState([])
  //const { id } = params


  useEffect(() => {
    setLoading(true)
    axios.get(`http://localhost:4000/articles/${idArticle}`)
      .then(res => {
        setArticle(res.data)
        setLoading(false)
      })
  }, [])

  if (loading) {
    return (
      <div>
        Loading...
      </div>
    )
  }

  return (
    <div className="App">
      <h2></h2>
    </div>
  )
}

export default App
